import Games from "./games";
import { useEffect, useState } from "react";
import axios from "axios";

const Read = () => {
  const [games, setGames] = useState([]);

  // Fetch games data from the backend
  useEffect(() => {
    axios.get('http://localhost:4000/api/games')
      .then((response) => {
        console.log(response.data);
        setGames(response.data); // Removed `.games` to align with likely backend response structure
      })
      .catch((error) => {
        console.error("Error fetching games:", error);
      });
  }, []);

  return (
    <div className="container my-5">
      <h3 className="text-center mb-4">Available Games</h3>
      {/* Game cards grid */}
      <div className="row">
        {games.length > 0 ? (
          <Games myGames={games} />
        ) : (
          <p className="text-center">No games available. Please add some!</p>
        )}
      </div>
    </div>
  );
};

export default Read;
