import axios from "axios";
import { useState } from "react";

const Create = () => {
  const [title, setTitle] = useState('');
  const [genre, setGenre] = useState('');
  const [price, setPrice] = useState('');
  const [releaseDate, setReleaseDate] = useState('');
  const [imageUrl, setImageUrl] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    const game = { title, genre, price, releaseDate, imageUrl };
    console.log(game);

    axios.post('http://localhost:4000/api/games', game)
      .then((res) => { console.log(res.data); })
      .catch();
  }

  return (
    <div>
      <h3>Add a New Game</h3>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label>Game Title:</label>
          <input type="text" className="form-control" value={title} onChange={(e) => setTitle(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Genre:</label>
          <input type="text" className="form-control" value={genre} onChange={(e) => setGenre(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Price:</label>
          <input type="number" className="form-control" value={price} onChange={(e) => setPrice(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Release Date:</label>
          <input type="date" className="form-control" value={releaseDate} onChange={(e) => setReleaseDate(e.target.value)} />
        </div>
        <div className="form-group">
          <label>Image URL:</label>
          <input type="text" className="form-control" value={imageUrl} onChange={(e) => setImageUrl(e.target.value)} />
        </div>
        <button type="submit">Add Game</button>
      </form>
    </div>
  );
};

export default Create;
